settings.liquid:
{% comment %}Tabela de Medidas - OTIMIZADA PAGESPEED{% endcomment %}

{% if product.metafields.custom.imagem_tabela_medidas != blank or product.metafields.custom.tabela_medidas != blank %}
<div class="size-guide-wrapper" style="margin:10px 0">

  <button type="button" onclick="openSizeGuide()" style="background:#fff;color:#000;border:1px solid #000;padding:6px 12px;cursor:pointer;font-size:12px;font-weight:900">
    Guia de Tamanhos
  </button>

  <div id="sizeGuideModal" style="display:none;position:fixed;z-index:9999;left:0;top:0;width:100%;height:100%;background:rgba(0,0,0,0.5)">
    <div class="size-guide-content" style="background:#fff;margin:5% auto;padding:15px 20px;border-radius:8px;width:90%;max-width:900px;max-height:85vh;overflow-y:auto;position:relative">

      <div style="display:flex;justify-content:space-between;align-items:center;margin-bottom:15px">
        {% if product.metafields.custom.imagem_tabela_medidas != blank %}
        <h3 style="margin:0;font-size:18px;font-weight:600">Como Medir</h3>
        {% elsif product.metafields.custom.tabela_medidas != blank %}
        <h3 style="margin:0;font-size:18px;font-weight:600">Tabela de Medidas</h3>
        {% else %}
        <h3 style="margin:0;font-size:18px;font-weight:600">Guia de Tamanhos</h3>
        {% endif %}

        <button onclick="closeSizeGuide()" style="background:none;border:none;font-size:28px;cursor:pointer;color:#666;line-height:1;padding:0">&times;</button>
      </div>

      {% if product.metafields.custom.tabela_descricao != blank %}
      <p style="color:#666;margin-bottom:15px;line-height:1.5;font-size:14px">{{ product.metafields.custom.tabela_descricao }}</p>
      {% endif %}

      {% if product.metafields.custom.imagem_tabela_medidas != blank %}
      <div style="margin-bottom:20px;text-align:center">
        <img id="sizeGuideImage" data-src="{{ product.metafields.custom.imagem_tabela_medidas | image_url: width: 800 }}" alt="Como Medir" loading="lazy" style="max-width:100%;height:auto;display:block;margin:0 auto">
      </div>
      {% endif %}

      {% if product.metafields.custom.tabela_medidas != blank %}
      <div style="margin-top:20px">
        <h3 style="font-size:18px;margin-bottom:10px;font-weight:600;text-align:center">Tabela de Medidas</h3>
        <p style="text-align:center;font-size:12px;color:#999;margin-bottom:10px;font-style:italic">‚Üê Deslize para ver todas as medidas ‚Üí</p>

        <div id="tableContainer" style="overflow-x:auto">
          {{ product.metafields.custom.tabela_medidas }}
        </div>
      </div>
      {% endif %}

      {% if product.metafields.custom.tabela_dica != blank %}
      <div style="margin-top:20px;padding:12px;background:#f8f8f8;border-left:4px solid #000;border-radius:4px">
        <strong style="display:block;margin-bottom:5px;font-size:14px">üí° Dica:</strong>
        <span style="font-size:14px">{{ product.metafields.custom.tabela_dica }}</span>
      </div>
      {% endif %}

    </div>
  </div>

</div>

<style>
.size-guide-content table{width:100%;border-collapse:collapse;margin:0;min-width:500px}
.size-guide-content table th,.size-guide-content table td{padding:10px 12px;text-align:left;border:1px solid #ddd;white-space:nowrap}
.size-guide-content table th{background:#f8f8f8;font-weight:600;text-transform:uppercase;font-size:11px;letter-spacing:.5px}
#tableContainer::-webkit-scrollbar{height:8px}
#tableContainer::-webkit-scrollbar-track{background:#f1f1f1;border-radius:4px}
#tableContainer::-webkit-scrollbar-thumb{background:#888;border-radius:4px}
#tableContainer::-webkit-scrollbar-thumb:hover{background:#555}
@media (max-width:768px){.size-guide-content{margin:2% auto;padding:12px 15px;width:95%}}
</style>

<script>
let imgLoaded=false;
function openSizeGuide(){
  const m=document.getElementById('sizeGuideModal');
  const img=document.getElementById('sizeGuideImage');
  if(img&&!imgLoaded){
    img.src=img.getAttribute('data-src');
    imgLoaded=true;
  }
  m.style.display='block';
  document.body.style.overflow='hidden';
}
function closeSizeGuide(){
  document.getElementById('sizeGuideModal').style.display='none';
  document.body.style.overflow='auto';
}
window.onclick=e=>{if(e.target==document.getElementById('sizeGuideModal'))closeSizeGuide()}
document.addEventListener('keydown',e=>{if(e.key==='Escape')closeSizeGuide()});
</script>

{% endif %}
